let nav=document.querySelector("body .navbar .nav-links");let navContainer=document.querySelector("body .navbar");let ham=document.querySelector(".menu-icon");let ranOnce=false;document.querySelector(".menu-icon").addEventListener("click", ()=>{document.querySelector(".side-bar").classList.toggle("closed");});function makeResponsive(){if(screen.width >=1155){if(navContainer.classList.contains("side-bar")){navContainer.classList.remove("side-bar");}if(ham.classList.contains("hidden")==false){ham.classList.add("hidden");}nav.classList.add("nav-right"); document.querySelector(".container").style.marginTop="6rem";}else{if(nav.classList.contains("nav-right")){nav.classList.remove("nav-right");}document.querySelectorAll(".navbar .nav-links ul li").forEach(element=>{element.classList.add("side-bar-link");}); if(ham.classList.contains("hidden")){ham.classList.remove("hidden");}if(ranOnce==false){setTimeout(()=>{navContainer.classList.add("closed");}, 1000); ranOnce=true;}navContainer.classList.add("side-bar"); document.querySelector(".container").style.marginTop="1rem";}}setInterval(makeResponsive, 1000);